targets.rawSql:
select count(onuid) as 'Total Autorizadas' from ftth_onu onu
inner join ftth_olt olt on (onu.olt_fk_id = olt.id)
where olt.nome like '%$olt%' and onu.status in ('0','1','2','3','4','online','offline','working','UP','DOWN','LOS','DyingGasp','LOS','offLine');

select count(onuid) as 'Total Onlines' from ftth_onu onu
inner join ftth_olt olt on (onu.olt_fk_id = olt.id)
where olt.nome like '%$olt%' and onu.status in ('1','online','working','UP');

select count(onuid) 'Total Offlines' from ftth_onu onu
inner join ftth_olt olt on (onu.olt_fk_id = olt.id)
where olt.nome like '%$olt%' and onu.status in ('2','offline','DOWN','OffLine');

select count(onuid) as 'Total Rompimento' from ftth_onu onu
inner join ftth_olt olt on (onu.olt_fk_id = olt.id)
where olt.nome like "%$olt%" and (onu.status in ('0','LOS','los') or (onu.status in ('offline') and onu.reason in ('LOSi/LOBi','los')));

select count(onuid) as 'Falta de Energia' from ftth_onu onu
inner join ftth_olt olt on (onu.olt_fk_id = olt.id)
where olt.nome like '%$olt%' and (onu.status in ('4','dying') or (onu.status in ('offline') and onu.reason in ('dying-gasp','DyingGasp')));

